export const categoryBoxStyle = () => {
  return {
    display: "block",
    overflowX: "auto",
    background: "white",
    margin: "10px",
    width: "100%",
    boxSizing: "border-box",
  };
};

export const listItemsStyle = () => {
  return {
    width: "100%",
    height: "100%",
    // display: "block",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    boxSizing: "order-box",
    margin: "0",
    pading: "0",
    // flexWrap: "wrap",
    overflowX: "auto",
  };
};
