import React from "react";
import { Hook } from "./hook";

import cartIconURL from "./resources/cart-icon.svg";

import { ProductUI } from "../UI";
import { AllCategory } from "../AllCategory";
import { Brands } from "../Brands";
import { Category } from "../Category/index";
import { Sorting } from "../Sort";

import {
  homePageStyle,
  headerPageStyle,
  logoBoxStyle,
  logoTitleStyle,
  cartBoxStyle,
  cartIconStyle,
  homeBodyStyle,
} from "./style";

const Home = () => {
  const [listItems, setItemList] = React.useState([]);
  // const [updatedList, setUpdatedList] = React.useState([]);
  let cate = React.useRef([]);
  let brands = React.useRef([]);
  let updatedList = React.useRef([]);

  console.log("updatedList", updatedList?.current);

  /**
   *
   * brand select handler
   */
  const brandHandler = (brand) => {
    if (brand && brand?.length) {
      let list = [];
      listItems?.forEach((item, index) => {
        if (item?.brand?.toLowerCase() === brand?.toLowerCase()) {
          list.push(item);
        }
      });
      //setUpdatedList(list);
      updatedList.current = list;
    }
  };

  /***
   * category select handler
   */
  const categoryHandler = (category) => {
    if (category && category?.length) {
      let list = [];
      listItems?.forEach((item, index) => {
        if (item?.category?.toLowerCase() === category?.toLowerCase()) {
          list.push(item);
        }
      });
      // setUpdatedList(list);
      updatedList.current = list;
    }
  };

  /**
   * sorting handler
   */
  const sortingHandler = (via) => {
    console.log(via);
    if (via === "pricelow") {
      //setUpdatedList(listItems?.sort((a, b) => a?.price > b?.price));
      updatedList.current = listItems?.sort((a, b) => a?.price > b?.price);
    } else if (via === "pricehigh") {
      //setUpdatedList(listItems?.sort((a, b) => a?.price < b?.price));
      updatedList.current = listItems?.sort((a, b) => a?.price < b?.price);
    } else if (via === "discount") {
      // setUpdatedList(
      //   listItems?.sort((a, b) => a?.discountPercentage < b?.discountPercentage)
      // );
    } else if (via === "rating") {
      // setUpdatedList(listItems?.sort((a, b) => a?.rating < b?.rating));
    }
  };

  React.useEffect(() => {
    if (updatedList && updatedList?.current?.length) {
      updatedList?.current?.forEach((item) => {
        if (!cate?.current?.includes(item.category)) {
          cate?.current?.push(item?.category);
        }
        if (!brands?.current?.includes(item.brand)) {
          brands?.current?.push(item?.brand);
        }
      });
    }
  }, [updatedList]);

  Hook(setItemList, updatedList);
  return (
    <div className="home--page" style={homePageStyle()}>
      <div className="header--page" style={headerPageStyle()}>
        <div className="logo--box" style={logoBoxStyle()}>
          <h1 className="logo--title" style={logoTitleStyle()}>
            Shop<span style={{ color: "red" }}>Now</span>
          </h1>
        </div>
        <div style={{ display: "flex" }}>
          <Brands
            onSelect={brandHandler}
            brand={brands?.current}
            category={cate?.current}
          />
          <Category
            brand={brands?.current}
            category={cate?.current}
            onSelect={categoryHandler}
          />
        </div>
        <Sorting onSelect={sortingHandler} />
        <div className="cart--box" style={cartBoxStyle()}>
          <span className="cart" style={cartIconStyle(cartIconURL)}></span>
        </div>
      </div>
      <div className="home--body" style={homeBodyStyle()}>
        <AllCategory
          category={cate?.current}
          listItems={updatedList?.current}
        />
      </div>
    </div>
  );
};

export { Home };
