import React from "react";
import { Hook } from "./hook";

import cartIconURL from "./resources/cart-icon.svg";

import { ProductUI } from "../UI";
import { Category } from "../Category";
import { Brands } from "../Brands";

import {
  homePageStyle,
  headerPageStyle,
  logoBoxStyle,
  logoTitleStyle,
  cartBoxStyle,
  cartIconStyle,
  homeBodyStyle,
} from "./style";

const Home = () => {
  const [listItems, setItemList] = React.useState([]);
  const [selectedBrand, setBrand] = React.useState("");

  // const [category, setCategory] = React.useState([]);
  //console.log(listItems);
  console.log("brand", selectedBrand);
  console.log("listItems", listItems);
  const brandHandler = (brand) => {
    setBrand(brand);

    if (selectedBrand && selectedBrand?.length) {
      let list = [];
      listItems?.forEach((item, index) => {
        if (
          item?.brand?.text.toLowerCase() === selectedBrand?.text.toLowerCase()
        ) {
          list.push(item);
        }
      });
      setItemList(list);
    }
  };

  let renderListItems = null;
  let cate = React.useRef([]);
  let brands = React.useRef([]);

  if (listItems && listItems.length) {
    listItems.forEach((item) => {
      if (!cate?.current?.includes(item.category)) {
        cate?.current?.push(item?.category);
      }
      if (!brands?.current?.includes(item.brand)) {
        brands?.current?.push(item?.brand);
      }
    });

    //console.log(" cate.current", cate?.current);
    // let style = {
    //   width: "230px",
    //   height: "250px",
    // };
    // renderListItems = listItems?.map((item, index) => {
    //   return (
    //     <ProductUI
    //       style={style}
    //       key={item?.id}
    //       name={item?.brand}
    //       rating={item?.rating}
    //       stock={item?.stock}
    //       category={item?.category}
    //       image={item?.thumbnail}
    //       price={item?.price}
    //       offer={item?.discountPercentage}
    //     />
    //   );
    // });
  }

  Hook(setItemList);
  return (
    <div className="home--page" style={homePageStyle()}>
      <div className="header--page" style={headerPageStyle()}>
        <div className="logo--box" style={logoBoxStyle()}>
          <h1 className="logo--title" style={logoTitleStyle()}>
            Shop<span style={{ color: "red" }}>Now</span>
          </h1>
        </div>
        <Brands
          onSelect={brandHandler}
          brand={brands?.current}
          category={cate?.current}
        />
        <div className="cart--box" style={cartBoxStyle()}>
          <span className="cart" style={cartIconStyle(cartIconURL)}></span>
        </div>
      </div>
      <div className="home--body" style={homeBodyStyle()}>
        {/* {renderListItems} */}
        <Category category={cate?.current} listItems={listItems} />
      </div>
    </div>
  );
};

export { Home };
