export const categoryBoxStyle = () => {
  return {
    display: "block",
    overflowX: "auto",
    background: "white",
    margin: "10px",
    width: "100%",
    boxSizing: "border-box",
    boxShadow: "rgba(100, 100, 111, 0.2) 0px 7px 29px 0px",
  };
};

export const listItemsStyle = () => {
  return {
    width: "100%",
    height: "100%",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    boxSizing: "order-box",
    marginBottom: "10px",
    padding: "0",
    overflowX: "auto",
  };
};
