import React from "react";
import { ProductUI } from "../UI/index";

import { categoryBoxStyle, listItemsStyle } from "./style";

let renderListItems = null;

const Category = (props) => {
  const letUI = (item, style) => {
    return (
      <ProductUI
        style={style}
        key={item?.id}
        name={item?.brand}
        rating={item?.rating}
        stock={item?.stock}
        category={item?.category}
        image={item?.thumbnail}
        price={item?.price}
        offer={item?.discountPercentage}
      />
    );
  };

  let renderCatgoryItems = (props) => {
    let style = {
      width: "230px",
      height: "250px",
    };
    let title = null;
    let list = null;
    let category = null;
    for (let i = 0; i < props?.category.length; i++) {
      title = <h3>{props?.category[i]}</h3>;
      for (let j = 0; j < props.listItems; j++) {
        renderListItems = props?.listItems?.map((item, index) => {
          if (props?.category && props?.listItems?.category) {
            letUI(props?.listItems[i], style);
          }
        });
      }
      category = (
        <div style={categoryBoxStyle()}>
          {title}
          <div className="products--item--wrapper" style={listItemsStyle()}>
            {renderListItems}
          </div>
        </div>
      );
      list.push(category);
    }

    return list;
  };

  return renderCatgoryItems(props);
};

export { Category };
